import { body, check } from "express-validator";

export const checkLogin = [
  check("email").notEmpty().withMessage("Yu cu nhp email"),
  body("email").isEmail().withMessage("Yu cu nhp ng nh dng"),
  body("password")
    .isLength({ min: 6 })
    .withMessage("Mt khu c it nht 6 k t"),
];

